SELECT 

FROM wsxd_remind_record a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.loan_bill_no = #{loanBillNo}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM wsxd_remind_record a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
