mapper.select:
SELECT 

FROM wsxd_repay_hst a

WHERE a.id = #{id}

SELECT 

FROM wsxd_repay_hst a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM wsxd_repay_hst a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE wsxd_repay_hst SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
