mapper.select:
SELECT 

FROM wsxd_repay_plan a

WHERE a.id = #{id}

SELECT 

FROM wsxd_repay_plan a


	a.loan_bill_no = #{loanBillNo}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM wsxd_repay_plan a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE wsxd_repay_plan SET 	
	del_flag = #{delFlag}
WHERE id = #{id}

UPDATE wsxd_repay_plan SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
