mapper.select:
SELECT 

FROM wsxd_sms_template a

WHERE a.id = #{id}

SELECT 

FROM wsxd_sms_template a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.min_overdue_day = #{minOverdueDay}


		AND a.max_overdue_day = #{maxOverdueDay}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM wsxd_sms_template a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE wsxd_sms_template SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
