mapper.select:
SELECT g.*, s.id AS s_id, s.group_name AS s_group_name, s.group_id AS s_group_id, s.app_org AS s_app_org, s.app_org_name AS s_app_org_name, s.department_id AS s_department_id, s.department_name AS s_department_name, s.has_common_pool AS s_has_common_pool, s.create_date as s_create_date, s.update_date as s_update_date FROM wsxd_allocate_group g LEFT JOIN wsxd_allocate_group_scope s ON g.id = s.group_id
g.del_flag = #{DEL_FLAG_NORMAL}
group by g.id
ORDER BY ${page.orderBy}
ORDER BY g.update_date DESC

SELECT 

FROM wsxd_allocate_group a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE wsxd_allocate_group
group_name = #{groupName}, odvs = #{odvs}, odvs_name = #{odvsName}, min_overdue_day = #{minOverdueDay}, max_overdue_day = #{maxOverdueDay}, status = #{status}, create_by = #{createBy.id}, create_date = #{createDate}, update_by = #{updateBy.id}, update_date = #{updateDate}
WHERE id = #{id}

UPDATE wsxd_allocate_group SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT id, odvs AS odv, odvs_name AS odvName FROM wsxd_allocate_group AS a
a.status = '1' and del_flag = '0'
and a.id = #{_parameter}

SELECT
g.*,
s.id AS s_id,
s.group_name AS s_group_name,
s.group_id AS s_group_id,
s.app_org AS s_app_org,
s.app_org_name AS s_app_org_name,
s.department_id AS s_department_id,
s.department_name AS s_department_name,
s.has_common_pool AS s_has_common_pool,
s.create_by as s_create_by,
s.create_date as s_create_date,
s.update_by as s_update_by,
s.update_date as s_update_date
FROM
wsxd_allocate_group g
LEFT JOIN wsxd_allocate_group_scope s ON g.id = s.group_id

	 AND g.id = #{id} 
	 AND g.group_name = #{groupName} 
	 AND g.odvs = #{odvs} 
	 AND g.odvs_name = #{odvsName} 
	 AND g.min_overdue_day = #{minOverdueDay} 
	 AND g.max_overdue_day = #{maxOverdueDay} 
	 AND g.status = #{status} 
	 AND g.create_by = #{createBy.id} 
	 AND g.create_date = #{createDate} 
	 AND g.update_by = #{updateBy.id} 
	 AND g.update_date = #{updateDate} 
	 AND g.remarks = #{remarks} 
	 AND g.del_flag = #{delFlag}
