mapper.select:
SELECT 

FROM wsxd_allocate_rule a

WHERE a.id = #{id}

SELECT 

FROM wsxd_allocate_rule a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.num ASC

SELECT 

FROM wsxd_allocate_rule a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

